import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

  let query = useQuery();

  const page = parseInt(query.get("page")) ? parseInt(query.get("page")) : 1

  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles?page=${page}`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
    };
    fetchData();
  }, [page]);

  return (
    <div>
      <Navbar />
      {previous ? <NavLink className="btn-primary btn" to={`/?page=${page - 1}`}>Previous</NavLink> : null}
      {next ? <NavLink className="btn-primary btn" to={`/?page=${page + 1}`}>Next</NavLink> : null}
      
      
      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
