import React, {useEffect, useState} from 'react';

import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import Homepage from './pages/Homepage';
import Authors from './pages/Authors';
import Author from './pages/Author';
import Article from './pages/Article';

function App() {
  const [isFetching, setIsFetching] = useState(true)
  const [articles, setArticles] = useState()
  useEffect(() => {
    const fetchData = async () => {
      const articles = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/articles`).then(res => res.json())
      const authors = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/authors`).then(res => res.json())
      const comments = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/comments`).then(res => res.json())
      const tags = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/tags`).then(res => res.json())
  
        setIsFetching(false)
        setArticles( 
          articles
        )
    }
    fetchData();
  })



  return (
    <Router>
      <Switch>
          <Route exact path="/">
            <Homepage
              articles={articles}
            />
          </Route>
          <Route exact path="/article/:id">
            <Article/>
          </Route>
          <Route exact path="/authors">
            <Authors/>
          </Route>
          <Route exact path="/author/:id">
            <Author/>
          </Route>
      </Switch>
    </Router>
  );
}

export default App;
