import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";
import Comments from "../components/Comments";

const Article = ({ allTags }) => {
  const url = process.env.REACT_APP_API_ENDPOINT;

  const [article, setArticle] = useState({});
  const [author, setAuthor] = useState({});
  const [comments, setComments] = useState([]);

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
            const setData = [setArticle, setAuthor, setComments];

      const dataArticle = await fetch(`${url}/articles/${id}`).then((res) =>
        res.json()
      );

      setArticle(dataArticle);
      setAuthor(dataArticle.author);
      setComments(dataArticle.comments);
    };
    fetchData();
  }, [id, url]);
  console.log(comments);
  return (
    <div>
      <Navbar />
      <AuthorInfo author={author} />
      <ArticleInfo article={article} allTags={allTags} />
      <div className="bg-primary pt-5 pb-4">
        <div className="container">
          <h1>
            <b>Comments</b>
          </h1>
        </div>
      </div>
      <div className="container" key="cards">
        <div className="row">
          {comments.map((comment) => (
            <Comments comment={comment} key={comment.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Article;
