import React, { useEffect, useState } from "react";

import {
  useLocation
} from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";
import Pagination from "../components/Pagination";

import {querySetter1} from "../helpers/querySetter";

const Homepage = ({ allTags }) => {


  const [data, setData] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${query}`
      ).then((res) => res.json());

      setData(dataArticles);
    };
    fetchData();
  }, [query]);

  
  const { articles } = data;
  console.log(querySetter1("page", data.page, query))
  return (
    <div>
      <Navbar setQuery={setQuery} />
      <Pagination data={data} setQuery={setQuery} query={query} />
      <div className="container" key="cards">
        <div className="row">
          {articles &&
            articles.map((article) => (
              <Card
                article={article}
                allTags={allTags}
                setQuery={setQuery}
                key={article.id}
                query={query}
              />
            ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
