import React, { useEffect, useState } from "react";

import { useLocation } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";
/* import Pagination from "../components/Pagination"; */

function useQuery() {
  return new URLSearchParams(useLocation().search);
}




const Homepage = ({ allTags }) => {
  let querys = useQuery();
  const [data, setData] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${query}`
      ).then((res) => res.json());

      setData(dataArticles);
    };
    fetchData();
  }, [query]);

  const { articles, next, previous, pages, page, limit, tag } = data;
  console.log(querys.get("page"))
  return (
    <div>
      <Navbar setQuery={setQuery} />
      {/* <Pagination
        data={data}
        setQuery={setQuery}
        query={query}
      /> */}
      <div className="container" key="cards">
        <div className="row">
          {articles &&
            articles.map((article) => (
              <Card
                article={article}
                allTags={allTags}
                setQuery={setQuery}
                key={article.id}
              />
            ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
