import React from "react";

import { NavLink } from "react-router-dom";

const ArticleInfo = ({ article, allTags }) => {
  const { id, title, date, tags, body } = article;

  return (
    <div className="bg-dark pb-3 pt-3">
      <div className="container">
        <NavLink
          className="text-light text-decoration-none"
          to={`./article/${id}`}
        >
          <h2>{title}</h2>
        </NavLink>
        <p className="text-light mt-2 mb-0">{date}</p>
        {/* <NavLink className="text-primary text-decoration-none pr-2" to="/">{tags}</NavLink> */}
        {tags ? tags.map((tag) => (
          <NavLink
            className="text-primary text-decoration-none pr-2"
            to={`/tag/${tag}`}
            key={tag}
          >
            {allTags[tag]}
          </NavLink>
        ))
          : ''
      }
        
        
      </div>
      <div className="container text-light article-content">
        {body}
      </div>
    </div>
  );
};

export default ArticleInfo;
