import React, { useEffect, useState } from "react";

import { useLocation } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorsCard from "../components/AuthorsCard";
import Pagination from "../components/Pagination";

const Authors = () => {
  const [authors, setAuthors] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataAuthors = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors${query}`
      ).then((res) => res.json());

      setAuthors(dataAuthors);
    };
    fetchData();
  }, [query]);

  

  return (
    <div>
      <Navbar
        setQuery={setQuery}
      />
      <Pagination data={authors} setQuery={setQuery} query={query} />
      <div className="container" key="cards">
        <div className="row">
          {authors.arr.map((author) => (
            <AuthorsCard author={author} key={author.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Authors;
