import React from "react";

import { NavLink } from "react-router-dom";

const AuthorInfo = ({ author }) => {
  console.log(author);
  const { avatar, name, id, username, email, website, bio } = author;
  return (
    <div className="bg-primary pt-5 pb-4">
      <div className="container">
        <div className="float-left pr-4">
          <NavLink
            className="text-light text-decoration-none"
            to={`./author/${id}`}
          >
            <img
              className="rounded-circle border border-primary img-border"
              alt="Avatar"
              src={avatar}
            />
          </NavLink>
        </div>
        <h1 className="text-light mt-0">{name}</h1>
        <NavLink
          className="text-light text-decoration-none"
          to={`./author/${id}`}
        >
          <h6>@{username}</h6>
        </NavLink>
        <NavLink
          className="text-light text-decoration-none"
          to={`mailto: ${email}`}
        >
          <i className="fa fa-envelope pr-2">{email}</i>
        </NavLink>
        <br />
        {/* <NavLink className="text-light text-decoration-none" to={website}>
          <i className="fa fa-globe pr-2">{website}</i>
        </NavLink> */}
        <p className="m-bio text-light">"{bio}"</p>
      </div>
    </div>
  );
};

export default AuthorInfo;
