import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";
import Comments from "../components/Comments";


const Article = ({allTags}) => {
  const url = process.env.REACT_APP_API_ENDPOINT
  
  const [article, setArticle] = useState({});
  const [author, setAuthor] = useState({});
  const [comments, setComments] = useState([]);

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {

/*       const setData = [setArticle, setAuthor, setComments];
      
      
      Promise.all([
        fetch(
          `${url}/articles/${id}`
        ).then((res) => res.json()),
        fetch(
          `${url}/articles/${id}/author`
        ).then((res) => res.json()),
        fetch(
          `${url}/articles/${id}/comments`
        ).then((res) => res.json()),
      ]).then((values) => values.map((value, ix) => {
        return setData[ix](value)
      })); */

      const dataArticle = await fetch(
        `${url}/articles/${id}`
      ).then((res) => res.json());
 
      setArticle(dataArticle)
      setAuthor(dataArticle.author)
      setComments(dataArticle.comments)

    };
    fetchData();
  }, [id, url]);
  console.log(comments)
  return (
    <div>
      <Navbar />
      <AuthorInfo author={author} />
      <ArticleInfo article={article} allTags={allTags} />
      <div className="container" key="cards">
        <div className="row">
        <div className="bg-primary pt-5 pb-4">
        <div className="container">
          <b>Comments</b>
        </div>
        </div>
          {comments.map((comment) => (
            <Comments comment={comment} key={comment.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Article;
