import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";

const Article = () => {
  const [article, setArticle] = useState([]);
  const [author, setAuthor] = useState([]);
  const [allTags, setAllTags] = useState([]);
  const [comments, setComments] = useState([]);

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      const dataArticle = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles/${id}`
      ).then((res) => res.json());

      const dataTags = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/tags`
      ).then((res) => res.json());

      const dataAuthor = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles/${id}/author`
      ).then((res) => res.json());

      const dataComments = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles/${id}/comments`
      ).then((res) => res.json());

      setComments(dataComments);
      setArticle(dataArticle);
      setAllTags(dataTags);
      setAuthor(dataAuthor);
      
    };
    fetchData();
  }, [id]);
  console.log(comments)
  console.log(author)
  console.log(article)
  console.log(allTags)
  return (
    <div>
      <Navbar />
      <AuthorInfo
        author={author}
      />
      <ArticleInfo
          article={article}
          allTags={allTags}
      />
      <div className="container" key="cards">
        <div className="row">
        </div>
      </div>
    </div>
  );
};

export default Article;
