import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

  let query = useQuery();
 /*  const limit = query.get("limit") ? query.get("limit") : 10 */


  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);
  const [page, setPage] = useState(1);
  const [limit, setLimit] = useState(10);
  const [tag, setTags] = useState("alltags");

  

  useEffect(() => {
    const fetchData = async () => {
      setPage(query.get("page"));
      setLimit(query.get("limit"));
      setTag(query.get("tag"));
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles?page=${[page]}&limit=${limit}&tag=${tag}`
      ).then((res) => res.json());
      

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      setPages(dataArticles.pages);
      
    };
    fetchData();
  }, [page, limit, tag]);

  return (
    <div>
      <Navbar />
      <div className="container text-center">
        
          <NavLink className={`btn-primary btn mr-2 ${previous.disabled}`} to={`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`} key="previous">
            Previous
          </NavLink>
       
        {pages.map((p, ix) => (
          <NavLink className={`btn-secondary btn ${p.disabled}`} to={`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`} key={`page${ix + 1}`}>
            {ix + 1}
          </NavLink>
        ))}
       
          <NavLink className={`btn-primary btn ml-2 ${next.disabled}`} to={`/?page=${next.page}&limit=${next.limit}&tag=${next.tag}`} key="next">
            Next
          </NavLink>
       
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
