import React, {useEffect, useState} from 'react';

import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

import Homepage from './pages/Homepage';
import Authors from './pages/Authors';
import Author from './pages/Author';
import Article from './pages/Article';

function App() {
  const articles = []
  const authors = []
  const comments = []
  const tags = []
  const [isFetching, setIsFetching] = useState(true)
  useEffect(() => {
    const fetchData = async () => {
      articles = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/articles`).then(res => res.json())
      authors = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/authors`).then(res => res.json())
      comments = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/comments`).then(res => res.json())
      tags = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/tags`).then(res => res.json())

        setIsFetching(false)

    }
    fetchData();
  })
  console.log(articles)
  return (
    <Router>
      <Switch>
          <Route exact path="/">
            <Homepage
              articles={articles}
            />
          </Route>
          <Route exact path="/article/:id">
            <Article/>
          </Route>
          <Route exact path="/authors">
            <Authors/>
          </Route>
          <Route exact path="/author/:id">
            <Author/>
          </Route>
      </Switch>
    </Router>
  );
}

export default App;
