const fetchData = async () => {
    const response = await fetch(process.env.REACT_APP_API_URL).then((res) => res.json());;
    return response;
};

const allAuthors = fetchData('authors');
const allArticles = (url) => {fetchData(`articles${url}`)};
const allComments = fetchData('comments');
const allTags = fetchData('tags');

/* const author = fetchData(`authors/${url}`); */

export { 
    allAuthors, 
    allArticles,
    allComments,
    allTags
}
