import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      setPages(dataArticles.pages);
    };
    fetchData();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="container text-center">
        <button
          className={`btn-primary btn mr-2 ${previous.disabled}`}
          key="previous"
          onClick={() => setTest(`?page=${previous.page}`)}
        >
          Previous
        </button>

        {pages.map((p, ix) => (
          <NavLink
            className={`btn-secondary btn ${p.disabled}`}
            to={`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`}
            key={`page${ix + 1}`}
          >
            {ix + 1}
          </NavLink>
        ))}

        <NavLink
          className={`btn-primary btn ml-2 ${next.disabled}`}
          to={`/?page=${next.page}&limit=${next.limit}&tag=${next.tag}`}
          key="next"
        >
          Next
        </NavLink>
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
