const querySetter = (key, value, query) => {
    const search = query
    const params = new URLSearchParams(search);
    const test = params.get("page");

    let q = query + "&";
    let re = new RegExp("[?|&]" + key + "=.*?&");
    console.log(re)
    if (!re.test(q))
        q += key + "=" + encodeURI(value);
    else
        q = q.replace(re, "&" + key + "=" + encodeURIComponent(value));
    q = q.trimStart("&").trimEnd("&");
    return q[0]==="?" ? q : q = "?" + q;
}

const querySetter1 = (key, value , query) => {


    const newKey = `${key}=`
    if(query === "") {
        return `?${newKey + value}`
    } else if (query.includes(newKey)) {
        return query.replace(newKey + test, newKey + value)
    }
}

export { 
    querySetter,
    querySetter1
}
