import React, {useEffect, useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [isFetching, setIsFetching] = useState(true)
  useEffect(() => {
    const fetchData = async () => {
      const articles = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/articles`).then(res => res.json())
      const authors = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/authors`).then(res => res.json())
      const comments = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/comments`).then(res => res.json())
      const tags = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/tags`).then(res => res.json())

        console.log(articles)
        console.log(authors)
        console.log(comments)
        console.log(tags)
        setIsFetching(false)

    }
    fetchData();
  })
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <p>
          {
            isFetching ? 'Fetching data...' : 'Data fetched'
          }
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
