const fetchData = async () => {
    const response = await fetch(process.env.REACT_APP_API_URL + url);
    const data = await response.json();
    return data;
};

const allAuthors = fetchData('authors');
const allArticles = fetchData('articles');
const allComments = fetchData('comments');
const allTags = fetchData('tags');
const article = fetchData(`articles/`);
const author = fetchData(`authors/`);

export { 
    allAuthors, 
    allArticles,
    allComments,
    allTags,
    article,
    author
}
