import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";
import AuthorInfo from "../components/AuthorInfo";

const Author = ({allTags}) => {
  const [author, setAuthor] = useState([]);
  const [authorArticles, setAuthorArticles] = useState([
    {
      id: 1,
      title: 'test',
      date: Date.now,
      summary: 'test',
      tags:['test'],
      authorId: 1,
      commentsCount: 1,
      author: {},

    }
  ]);

  const { id } = useParams();
  useEffect(() => {
    const fetchData = async () => {

      const dataAuthorArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}/articles`
      ).then((res) => res.json());


      setAuthor(dataAuthorArticles[0].author);
      setAuthorArticles(dataAuthorArticles);
    };
    fetchData();
  }, [id]);
  console.log(authorArticles)
  return (
    <div>
      <Navbar/>

      <AuthorInfo 
        author={author}
      />

      <div className="container" key="cards">
        <div className="row">
          {authorArticles && authorArticles.map((article) => (
            <Card
              author={author}
              allTags={allTags}
              key={article.id}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Author;
