import React from "react";

import { NavLink } from "react-router-dom";

const Comments = ({ comment }) => {
  const { date, body, author } = comment;


  return (
    <div className="container card">
      <p>{date}</p>

      {/* <NavLink to={`./author/${author.authorId}`}> */}
        <img
          className="rounded-circle border border-primary img-border mt-3"
          width={85}
          height={85}
          alt="Avatar"
          src={author ? author.avatar : "https://s3.amazonaws.com/uifaces/faces/twitter/bu7921/128.jpg"}
        />
      {/* </NavLink> */}

      <div>
        <p>{body}</p>
      </div>
    </div>
  );
};

export default Comments;
