import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";


const Homepage = ({ allTags }) => {
  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);
  const [page, setPage] = useState("?page=1");
  const [tag, setTag] = useState("");
  const [url, setUrl] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${url}`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      setPages(dataArticles.pages);
    };
    fetchData();
  }, [page, tag]);

  return (
    <div>
      <Navbar
        setPage={setPage}
        setTag={setTag}
      />
      <div className="container text-center">
        <NavLink
          className={`btn-primary btn mr-2 ${previous.disabled}`}
          key="previous"
          to={`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`}
          onClick={() => {
            setPage(`?page=${previous.page}`)
            setUrl(`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`)
          }}
        >
          Previous
        </NavLink>

        {pages.map((p, ix) => (
          <button
            className={`btn-secondary btn ${p.disabled}`}
            onClick={() => setPage(`?page=${ix + 1}`)}
            key={`page${ix + 1}`}
          >
            {ix + 1}
          </button>
        ))}

        <button
          className={`btn-primary btn ml-2 ${next.disabled}`}
          key="next"
          onClick={() => setPage(`?page=${next.page}`)}
        >
          Next
        </button>
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} setTag={setTag} setPage={setPage} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
