const querySetter = (key, value , query) => {
    const search = query
    const params = new URLSearchParams(search);
    const test = params.get("page");

    const newKey = `${key}=`
    if(query === "") {
        return `?${newKey + value}`
    } else if (query.includes(newKey)) {
        query.replace(newKey + test, newKey + value)
    }
}

export { 
    querySetter
}
