import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);
  const [page, setPage] = useState("");
  const [limit, setLimit] = useState("");
  const [tag, setTag] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${page + limit + tag}`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      setPages(dataArticles.pages);
    };
    fetchData();
  }, [page, limit, tag]);

  return (
    <div>
      <Navbar />
      <div className="container text-center">
        <button
          className={`btn-primary btn mr-2 ${previous.disabled}`}
          key="previous"
          onClick={() => setPage(`?page=${previous.page}`)}
        >
          Previous
        </button>

        {pages.map((p, ix) => (
          <button
            className={`btn-secondary btn ${p.disabled}`}
            to={`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`}
            key={`page${ix + 1}`}
          >
            {ix + 1}
          </button>
        ))}

        <button
          className={`btn-primary btn ml-2 ${next.disabled}`}
          key="next"
          onClick={() => setPage(`?page=${next.page}`)}
        >
          Next
        </button>
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
