import React from "react";

import { NavLink } from "react-router-dom";

const Card = ({ article, allTags }) => {
  const {
    id,
    title,
    date,
    summary,
    tags,
    authorId,
    commentsLength,
    author,
  } = article;

  return (
    <div className="card border border-dark mt-4 col-12">
      <div className="card-body">
        <NavLink to={`/article/${id}`}>
          <h6 className="float-right text-primary">
            {commentsLength} comments
          </h6>
        </NavLink>
        <NavLink
          className="text-primary text-decoration-none"
          to={`/article/${id}`}
        >
          <h5>{title}</h5>
        </NavLink>
        <p className="mb-3">{date}</p>
        <p className="card-text mb-1">{summary}</p>

        {tags.map((tag) => (
          <NavLink
            className="text-primary text-decoration-none pr-2"
            to={`/tag/${tag}`}
          >
            {allTags[tag]}
          </NavLink>
        ))}

        <div>
          <div className="float-left pr-3">
            <NavLink to={`./author/${authorId}`}>
              <img
                className="rounded-circle border border-primary img-border mt-3"
                width={85}
                height={85}
                alt="Avatar"
                src={author.avatar}
              />
            </NavLink>
          </div>
          <div className="float-left">
            <h6 className="card-subtitle mt-4 text-muted">Author:</h6>
            <NavLink
              className="text-primary text-decoration-none h-25 d-inline-block"
              to={`/author/${authorId}`}
            >
              <h4>{author.name}</h4>
            </NavLink>
          </div>
          <NavLink
            to={`/article/${id}`}
            className="float-right btn btn-primary mt-5"
          >
            Read article
          </NavLink>
        </div>
      </div>
    </div>
  );
};

export default Card;
