import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";


const Homepage = ({ allTags }) => {
  const [data, setData] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${query}`
      ).then((res) => res.json());

      setData(dataArticles);
    };
    fetchData();
  }, [query]);
  console.log(query)
  const {articles, next, previous, pages, page, limit, tag} = data

  return (
    <div>
      <Navbar
        setQuery={setQuery}
      />
      <div className="container" key="cards">
        <div className="row">
          {articles && articles.map((article) => (
            <Card article={article} allTags={allTags} setQuery={setQuery} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
