import React from "react";

import { NavLink } from "react-router-dom";

const Comments = ({ comment }) => {
  const { date, body, author } = comment;

  return (
    <div class="container bg-warning">
      <div class="pt-4 pb-4">
        {/* <div class="float-right btn-group">
          <button
            type="button"
            class="btn btn-secondary dropdown-toggle dropdown-toggle-split text-warning"
            data-toggle="dropdown"
            aria-haspopup="true"
            aria-expanded="false"
          >
            <span class="pr-2" id="current-sorting">
              Date (oldest)
            </span>
          </button>
          <div class="dropdown-menu">
            <a class="dropdown-item sort-by-drop-down date-o">Date (oldest)</a>
            <a class="dropdown-item sort-by-drop-down date-n">Date (newest)</a>
          </div>
        </div> */}
        <h2 class="text-dark mb-0">
          <b>Article commnets</b>
        </h2>
      </div>

      <div className="container card">
        <h3>Comment</h3>
        <p>{date}</p>
        {
          <NavLink to={`./author/${comment.userId}`}>
            <img
              className="rounded-circle border border-primary img-border mt-3"
              width={85}
              height={85}
              alt="Avatar"
              src={
                author
                  ? author.avatar
                  : "https://s3.amazonaws.com/uifaces/faces/twitter/yalozhkin/128.jpg"
              }
            />
          </NavLink>
        }

        <div>
          <p>{body}</p>
        </div>
      </div>
    </div>
  );
};

export default Comments;
