import React from 'react';

import { NavLink, Link } from 'react-router-dom';

const Card = ({article}) => {
    const {id, title, date, summary, tags, authorId, commentsLength, author} = article
    return (
    <div class="card border border-dark mt-4 col-12">
        <div class="card-body">
            <NavLink to={`/article/${ id }`}>
                <h6 class="float-right text-primary">{commentsLength} comments</h6>
            </NavLink>
            <NavLink class="text-primary text-decoration-none" to={`/article/${ id }`}>
                <h5>{ title }</h5>
            </NavLink>
            <p class="mb-3">{ date }</p>
            <p class="card-text mb-1">{ summary }</p>
            
            {tags.map(tag => (
                <NavLink class="text-primary text-decoration-none pr-2" to={`/tag/${tag}`}>
                    {tag}
                </NavLink>
            ))}

            <div>
                <div class='float-left pr-3'>
                <Link to={`./author/${ authorId }`}><img class="rounded-circle border border-warning img-border mt-3" style="width: 64px; height: 64px" src={ author.avatar }/></Link>
                </div>
                <div class="float-left ">
                    <h6 class="card-subtitle mt-4 text-muted">Author:</h6>
                    <NavLink class="text-primary text-decoration-none h-25 d-inline-block" to={`/author/${ authorId }`}>
                        <h4>{author.name}</h4>
                    </NavLink> 
                </div>
                <NavLink to={`/article/${ id }`} class="float-right btn btn-primary mt-5">Read article</NavLink>
            </div>
        </div>
    </div>
    );
};

export default Card;
