import React from "react";

import { NavLink } from "react-router-dom";

const Comments = ({ comment }) => {
  const { date, body, author } = comment;


  return (
    <div className="media comment-card">
      <p>{date}</p>
      {<NavLink className="text-decoration-none" to={`./author/${comment.userId}`}>
        <img
          className="rounded-circle border border-primary img-border img-size"
          width={85}
          height={85}
          alt="Avatar"
          src={author ? author.avatar : "https://s3.amazonaws.com/uifaces/faces/twitter/yalozhkin/128.jpg"}
        />
       </NavLink> }

      <div>
        <p>{body}</p>
      </div>
    </div>
  );
};

export default Comments;
