import React from "react";

import { NavLink } from "react-router-dom";

const Card = ({ article, allTags, setQuery, query }) => {
  const {
    id,
    title,
    date,
    summary,
    tags,
    authorId,
    commentsCount,
    author,
  } = article;

  return (
    <div className="card border border-dark mt-4 col-12" key={id}>
      <div className="card-body">
        <h6 className="float-right text-primary">Test</h6>
        <NavLink
          className="text-primary text-decoration-none"
          to={`/article/${id}`}
          key={`title${id}`}
        >
          <h5>{title}</h5>
        </NavLink>
        <p className="mb-3" key={`date${id}`}>
          {date}
        </p>
        <p className="card-text mb-1" key={`summary${id}`}>
          {summary}
        </p>

        {tags.map((tag) => (
          <NavLink
            className="btn-sm btn btn-primary mr-1"
            to={`/?tag=${tag}`}
            onClick={() => {
              setQuery(`/?tag=${tag}`);
            }}
            key={tag}
          >
            {allTags[tag]}
          </NavLink>
        ))}

        <div key={`author${id}`}>
          <div className="float-left pr-3">
            <NavLink to={`./author/${authorId}`}>
              <img
                className="rounded-circle border border-primary img-border mt-3"
                width={85}
                height={85}
                alt="Avatar"
                src={author.avatar}
              />
            </NavLink>
          </div>
          <div className="float-left" key={`date${id}`}>
            <h6 className="card-subtitle mt-4 text-muted" key={`text${id}`}>
              Author:
            </h6>
            <NavLink
              className="text-primary text-decoration-none h-25 d-inline-block"
              to={`/author/${authorId}`}
              key={`name${id}`}
            >
              <h4>{author.name}</h4>
            </NavLink>
          </div>
          <div className="float-left">
            <NavLink to={`/article/${id}`} key={`comment${id}`}>
              <h6 className="text-primary">
                {commentsCount} comments
              </h6>
            </NavLink>
          </div>
          <NavLink
            to={`/article/${id}`}
            className="float-right btn btn-primary mt-5"
            key={`button${id}`}
          >
            Read article
          </NavLink>
        </div>
      </div>
    </div>
  );
};

export default Card;
