import React from "react";

import { NavLink } from "react-router-dom";

import {querySetter} from "../helpers/querySetter";

const Pagination = () => {


  return (
    <div className="container text-center">
    <NavLink
      className={`btn-primary btn mr-2 ${previous}`}
      key="previous"
      to={`/?page=${page - 1}&limit=${limit}&tag=${tag}`}
      onClick={() => {
        setQuery(`/?page=${page - 1}&limit=${limit}&tag=${tag}`)
      }}
    >
      Previous
    </NavLink>

    {pages && pages.map((p, ix) => (
      <NavLink
        className={`btn-secondary btn ${p.disabled}`}
        to={`/?page=${ix + 1}&limit=${limit}&tag=${tag}`}
        onClick={() => setQuery(`/?page=${ix + 1}&limit=${limit}&tag=${tag}`)}
        key={`page${ix + 1}`}
      >
        {ix + 1}
      </NavLink>
    ))}

    <NavLink
      className={`btn-primary btn ml-2 ${next}`}
      key="next"
      to={`/?page=${page + 1}&limit=${limit}&tag=${tag}`}
      onClick={() => setQuery(`/?page=${page + 1}&limit=${limit}&tag=${tag}`)}
    >
      Next
    </NavLink>
  </div>
  );
};

export default Pagination;
