import React, { useEffect, useState } from "react";

import { useParams, useLocation } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";
import AuthorInfo from "../components/AuthorInfo";
import Pagination from "../components/Pagination";

const Author = ({allTags}) => {
  const [author, setAuthor] = useState([]);
  const [authorArticles, setAuthorArticles] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  const { id } = useParams();
  useEffect(() => {
    const fetchData = async () => {

      const data = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}/articles`
      ).then((res) => res.json());

      setAuthor(data.arr[0].author);
      setAuthorArticles(data.arr);
    };
    fetchData();
  }, [id, query]);
  return (
    <div>
      <Navbar
        setQuery={setQuery}
      />

      <AuthorInfo 
        author={author}
      />

  <Pagination data={data} setQuery={setQuery} query={query} />

      <div className="container" key="cards">
        <div className="row">
          {authorArticles && authorArticles.map((article) => (
            <Card
              article={article}
              allTags={allTags}
              key={article.id}
              setQuery={setQuery}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Author;
