import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";
import Comments from "../components/Comments";


const Test = ({allTags}) => {
  const url = process.env.REACT_APP_API_ENDPOINT
  
  const [article, setArticle] = useState({});
  const [author, setAuthor] = useState({});
  const [comments, setComments] = useState([]);


  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {

        const dataArticle = await fetch(
            `${url}/articles/${id}/test`
          ).then((res) => res.json());
     
          setArticle(dataArticle)
          setAuthor(dataArticle.author)
          setComments(dataArticle.comments)
    };
    fetchData();
    
  }, [id, url]);

  console.log(article)
  console.log(author)
  console.log(comments)

  if(article){
    return (
        <div>
          <Navbar />
          {/* <AuthorInfo author={article.author} /> */}
          <ArticleInfo article={article} allTags={allTags} />
          {/* <div className="container" key="cards">
            <div className="row">
              {article && article.comments.map((comment) => (
                <Comments comment={comment} key={comment.id} />
              ))}
            </div>
          </div> */}
        </div>
      );
  }

};

export default Test;
