import React from "react";

import { NavLink } from "react-router-dom";

const Card = ({ article }) => {
  const {
    id,
    title,
    date,
    summary,
    tags,
    authorId,
    commentsLength,
    author,
  } = article;
  return (
    <div class="card border border-dark mt-4 col-12" key={id}>
      <div class="card-body">
        <NavLink to={`/article/${id}`} key={`comment${id}`}>
          <h6 class="float-right text-primary">
            {commentsLength} comments
          </h6>
        </NavLink>
        <NavLink
          class="text-primary text-decoration-none"
          to={`/article/${id}`}
          key={`title${id}`}
        >
          <h5>{title}</h5>
        </NavLink>
        <p class="mb-3" key={`date${id}`}>
          {date}
        </p>
        <p class="card-text mb-1" key={`summary${id}`}>
          {summary}
        </p>

        {tags.map((tag) => (
          <NavLink
            class="text-primary text-decoration-none pr-2"
            to={`/tag/${tag}`}
            key={tag}
          >
            {tag}
          </NavLink>
        ))}

        <div key={`author${id}`}>
          <div class="float-left pr-3">
            <NavLink to={`./author/${authorId}`}>
              <img
                class="rounded-circle border border-primary img-border mt-3"
                width={85}
                height={85}
                alt="Avatar"
                src={author.avatar}
              />
            </NavLink>
          </div>
          <div class="float-left" key={`date${id}`}>
            <h6 class="card-subtitle mt-4 text-muted" key={`text${id}`}>
              Author:
            </h6>
            <NavLink
              class="text-primary text-decoration-none h-25 d-inline-block"
              to={`/author/${authorId}`}
              key={`name${id}`}
            >
              <h4>{author.name}</h4>
            </NavLink>
          </div>
          <NavLink
            to={`/article/${id}`}
            class="float-right btn btn-primary mt-5"
            key={`button${id}`}
          >
            Read article
          </NavLink>
        </div>
      </div>
    </div>
  );
};

export default Card;
