import React, { useEffect, useState } from "react";

import {
  BrowserRouter as Router,

  useParams
} from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Author = () => {
  const [author, setAuthor] = useState([]);
  const [authorArticles, setAuthorArticles] = useState([]);

  const { id } = useParams();
  useEffect(() => {
    const fetchData = async () => {
      const dataAuthor = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}`
      ).then((res) => res.json());

      const dataAuthorArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}/articles`
      ).then((res) => res.json());

      setAuthor(dataAuthor);
      setAuthorArticles(dataAuthorArticles);
    };
    fetchData();
  }, []);
  console.log(author)
  return (
    <div>
      <Navbar />
      <div className="container" key="cards">
        <div className="row">

            <Card
                arti
            />

        </div>
      </div>
    </div>
  );
};

export default Author;
