import React, { useEffect, useState } from "react";

import { useParams, useLocation } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";
import Comments from "../components/Comments";

const Article = ({ allTags }) => {
  const [data, setData] = useState({});
  const [article, setArticle] = useState({});
  const [author, setAuthor] = useState({});
  const [comments, setComments] = useState([]);
  const [query, setQuery] = useState(useLocation().search);

  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {

      const data = await fetch(`${process.env.REACT_APP_API_ENDPOINT}/articles/${id + query}`).then((res) =>
        res.json()
      );

      setData(data);
      setArticle(data.article);
      setAuthor(data.author);
      setComments(data.arr);
    };
    fetchData();
  }, [id, query]);

  return (
    <div>
      <Navbar
        setQuery={setQuery}
      />
      <AuthorInfo author={author} />
      <ArticleInfo article={article} allTags={allTags} setQuery={setQuery} />
      <div className="bg-primary pt-5 pb-4">
        <div className="container">
          <h1>
            <b>Comments</b>
          </h1>
        </div>
      </div>
      <div className="container" key="cards">
        <div className="row">
          {comments.map((comment) => (
            <Comments comment={comment} key={comment.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Article;
