import React from 'react';

import { NavLink, Link } from 'react-router-dom';

const Card = ({article}) => {
    const {id, title, date, summary, tags, authorId, commentsLength, author} = article
    return (
    <div className="card border border-dark mt-4 col-12" key={id}>
        <div className="card-body" key={`body${id}`}>
            <NavLink to={`/article/${ id }`} key={`comment-link${id}`}>
                <h6 className="float-right text-primary" key={`comment${id}`}>{commentsLength} comments</h6>
            </NavLink>
            <NavLink className="text-primary text-decoration-none" to={`/article/${ id }`} key={`title-link${id}`}>
                <h5 key={`title${id}`}>{ title }</h5>
            </NavLink>
            <p className="mb-3" key={`date${id}`}>{ date }</p>
            <p className="card-text mb-1" key={`summary${id}`}>{ summary }</p>
            
            {tags.map(tag => (
                <NavLink className="text-primary text-decoration-none pr-2" to={`/tag/${tag}`} key={tag}>
                    {tag}
                </NavLink>
            ))}

            <div key={`author${id}`}>
                <div className='float-left pr-3' key={`image-div${id}`}>
                    <NavLink to={`./author/${ authorId }`} key={`image-link${id}`}>
                        <img className="rounded-circle border border-primary img-border mt-3" src={ author.avatar } key={`image${id}`}/>
                    </NavLink>
                </div>
                <div className="float-left" key={`date${id}`}>
                    <h6 className="card-subtitle mt-4 text-muted" key={`text${id}`}>Author:</h6>
                    <NavLink className="text-primary text-decoration-none h-25 d-inline-block" to={`/author/${ authorId }`} key={`name-link${id}`}>
                        <h4 key={`name${id}`}>{author.name}</h4>
                    </NavLink> 
                </div>
                <NavLink to={`/article/${ id }`} className="float-right btn btn-primary mt-5" key={`button${id}`}>Read article</NavLink>
            </div>
        </div>
    </div>
    );
};

export default Card;
