import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import AuthorInfo from "../components/AuthorInfo";
import ArticleInfo from "../components/ArticleInfo";
import Comments from "../components/Comments";


const Article = ({allTags}) => {
  const url = process.env.REACT_APP_API_ENDPOINT
  
  const [dataForArticle, setDataForArticle] = useState([]);


  const { id } = useParams();

  useEffect(() => {
    const fetchData = async () => {

        const dataArticle = await fetch(
            `${url}/articles/${id}/test`
          ).then((res) => res.json());
     
          setDataForArticle(dataArticle)
    };
    fetchData();
  }, [id]);

  const {article, author, comments} = dataForArticle
  console.log(dataForArticle)

  return (
    <div>
      <Navbar />
      <AuthorInfo author={author} />
      <ArticleInfo article={article} allTags={allTags} />
      <div className="container" key="cards">
        <div className="row">
          {comments.map((comment) => (
            <Comments comment={comment} key={comment.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Article;
