import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";


const Homepage = ({ allTags }) => {
  const [data, setData] = useState([]);

  const [url, setUrl] = useState(useLocation().search);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${url}`
      ).then((res) => res.json());

      setData(dataArticles);
    };
    fetchData();
  }, [url]);

  const {articles, next, previous, pages, currentPage} = data

  return (
    <div>
      <Navbar
        setUrl={setUrl}
      />
      <div className="container text-center">
        <NavLink
          className={`btn-primary btn mr-2 ${previous.disabled}`}
          key="previous"
          to={`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`}
          onClick={() => {
            setUrl(`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`)
          }}
        >
          Previous
        </NavLink>

        {pages.map((p, ix) => (
          <NavLink
            className={`btn-secondary btn ${p.disabled}`}
            to={`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`}
            onClick={() => setUrl(`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`)}
            key={`page${ix + 1}`}
          >
            {ix + 1}
          </NavLink>
        ))}

        <NavLink
          className={`btn-primary btn ml-2 ${next.disabled}`}
          key="next"
          to={`/?page=${next.page}&limit=${next.limit}&tag=${next.tag}`}
          onClick={() => setUrl(`/?page=${next.page}&limit=${next.limit}&tag=${next.tag}`)}
        >
          Next
        </NavLink>
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} setUrl={setUrl} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
