import React from "react";

import { NavLink } from "react-router-dom";

const Comments = ({ comment }) => {
  const { date, body, author } = comment;


  return (
    <div className="card border-dark mt-2">
      {<NavLink className="text-decoration-none ml-3" to={`/author/${comment.userId}`}>
        <img
          className="rounded-circle border-primary img-border mt-5"
          width={85}
          height={85}
          alt="Avatar"
          src={author ? author.avatar : "https://iptc.org/wp-content/uploads/2018/05/avatar-anonymous-300x300.png"}
        />
       </NavLink> }
      <NavLink className="text-decoration-none" to={`/author/${comment.userId}`}>
        <h5 className="ml-3">{author ? author.name : "Blog User" }</h5>
      </NavLink>
       
      <p className="mb-3 ml-3">{date}</p>

      <div>
        <div className="ml-3" dangerouslySetInnerHTML={{body }} />
      </div>
    </div>
  );
};

export default Comments;
