import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
/*   function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

  let query = useQuery();

  const page = parseInt(query.get("page")) ? parseInt(query.get("page")) : 1; */

  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);
  const [url, setUrl] = useState("");
  const test = useLocation().search;

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${url}`
      ).then((res) => res.json());
      

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      dataArticles.pages[previous.page + 1].disabled = "disabled"
      setPages(dataArticles.pages);
      setUrl(test);
    };
    fetchData();
  }, [test, url]);
  console.log(pages)
  return (
    <div>
      <Navbar />
      <div className="container text-center">
        
          <NavLink className={`btn-primary btn mr-2 ${previous.disabled}`} to={`/?page=${previous.page}&limit=${previous.limit}&tag=${previous.tag}`}>
            Previous
          </NavLink>
       
        {pages.map((p, ix) => (
          <NavLink className={`btn-light btn ${p.disabled}`} to={`/?page=${ix + 1}&limit=${p.limit}&tag=${p.tag}`}>
            {ix + 1}
          </NavLink>
        ))}
       
          <NavLink className={`btn-primary btn ml-2 ${next.disabled}`} to={`/?page=${next.page}&limit=${next.limit}&tag=${next.tag}`}>
            Next
          </NavLink>
       
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
