import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

  let query = useQuery();

  const page = query.get("page")

  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles?page=${page}`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
    };
    fetchData();
  }, [page]);

  return (
    <div>
      <Navbar />
      <NavLink className="btn-primary" to={`/?page=${page - 1}`}>Previous</NavLink>
      <NavLink className="btn-primary" to={`/?page=${page + 1}`}>Next</NavLink>
      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
