import React, { useEffect, useState } from "react";

import { useLocation, NavLink } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";

const Homepage = ({ allTags }) => {
  function useQuery() {
    return new URLSearchParams(useLocation().search);
  }

  let query = useQuery();

  const page = parseInt(query.get("page")) ? parseInt(query.get("page")) : 1;

  const [articles, setArticles] = useState([]);
  const [next, setNext] = useState({});
  const [previous, setPrevious] = useState({});
  const [pages, setPages] = useState([]);
  const [url, setUrl] = useState("")
  const test = useLocation().search


  useEffect(() => {
    const fetchData = async () => {
      const dataArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/articles${url}`
      ).then((res) => res.json());

      setArticles(dataArticles.articles);
      setNext(dataArticles.next);
      setPrevious(dataArticles.previous);
      setPages(dataArticles.pages);
      setUrl(test)
    };
    fetchData();
  }, [page, test, url]);
  
  return (
    <div>
      <Navbar />
      <div className="container text-center">
        {previous ? (
          <NavLink className="btn-primary btn" to={`/?page=${page - 1}`}>
            Previous
          </NavLink>
        ) : null}
        {pages.map((p, ix) => (
          <NavLink className={`btn-${p} btn `} to={`/?page=${ix + 1}&limit=`}>
            {ix + 1}
          </NavLink>
        ))}
        {next ? (
          <NavLink className="btn-primary btn" to={`/?page=${page + 1}`}>
            Next
          </NavLink>
        ) : null}
      </div>

      <div className="container" key="cards">
        <div className="row">
          {articles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Homepage;
