import React from "react";

import { NavLink } from "react-router-dom";

import {querySetter} from "../helpers/querySetter";

const Pagination = ({data, setQuery, query}) => {

  const { next, previous, pages, page } = data;

  if(pages > 1) {
    return (
      <div className="container text-center">
      <NavLink
        className={`btn-primary btn mr-2 ${previous}`}
        key="previous"
        to={querySetter("page", page - 1, query)}
        onClick={() => {
          setQuery(querySetter("page", page - 1, query))
        }}
      >
        Previous
      </NavLink>
  
      {pages && pages.map((p, ix) => (
        <NavLink
          className={`btn-secondary btn ${p.disabled}`}
          to={querySetter("page", ix + 1, query)}
          onClick={() => setQuery(querySetter("page", ix + 1, query))}
          key={`page${ix + 1}`}
        >
          {ix + 1}
        </NavLink>
      ))}
  
      <NavLink
        className={`btn-primary btn ml-2 ${next}`}
        key="next"
        to={querySetter("page", page + 1, query)}
        onClick={() => setQuery(querySetter("page", page + 1, query))}
      >
        Next
      </NavLink>
    </div>
    );
  } else {
    return ""
  }
  
};

export default Pagination;
