import React, { useEffect, useState } from "react";

import { useParams } from "react-router-dom";

import Navbar from "../components/Navbar";
import Card from "../components/Card";
import AuthorInfo from "../components/AuthorInfo";

const Author = () => {
  const [author, setAuthor] = useState([]);
  const [authorArticles, setAuthorArticles] = useState([]);
  const [allTags, setAllTags] = useState([]);

  const { id } = useParams();
  useEffect(() => {
    const fetchData = async () => {
      const dataAuthor = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}`
      ).then((res) => res.json());

      const dataAuthorArticles = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/authors/${id}/articles`
      ).then((res) => res.json());

      const dataTags = await fetch(
        `${process.env.REACT_APP_API_ENDPOINT}/tags`
      ).then((res) => res.json());

      setAuthor(dataAuthor);
      setAuthorArticles(dataAuthorArticles);
      setAllTags(dataTags);
    };
    fetchData();
  }, []);
  console.log(authorArticles);
  console.log(author);
  return (
    <div>
      <Navbar/>

      <AuthorInfo 
        author={author}
      />

      <div className="container" key="cards">
        <div className="row">
          {authorArticles.map((article) => (
            <Card article={article} allTags={allTags} key={article.id} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Author;
